-- This adds a new path to the default Lua/TIC-80(?) path. 
-- In order to run the game locally you have to add:
--      ";complete/path/to/directory/?.lua"
-- to the package.path variable below
package.path = package.path..
                ";C:/Users/diogo/AppData/Roaming/com.nesbox.tic/TIC-80/retro-jam-2022/src/?.lua"
                
require "levels"
require "draw/draw-game"
require "draw/draw-cutscenes"
require "utils"
require "state-machine"
require "macros"
require "input"
require "calls"
require "ropes"
require "knobs"
require "messages"

function TIC()
    update()
    draw()
end

-- updates
function update()
    FRAME_COUNTER = FRAME_COUNTER + 1

    if has_value(SKIPPABLE_STATES, CUR_STATE) and keyp(Z_KEYCODE) then
        update_state_machine()
    elseif has_value(PLAYABLE_STATES, CUR_STATE) then
        update_mouse()
        update_ropes()
        update_knobs()
        update_calls()
        update_messages()

        if CUR_STATE ~= STATES.CALL_THIEF then
            -- Level's time is over
            if SECONDS_PASSED == LEVELS[CUR_STATE].time then update_state_machine()end
        else
            -- Thief's call was connected
            if CALLS[1].state == CALL_STATE.DISPATCHING then
                update_state_machine()
            end
        end
    elseif has_value({STATES.SELECT_MENU_1, STATES.SELECT_MENU_2}, CUR_STATE) then
        update_select_menu()
    elseif has_value(END_LEVEL_STATES, CUR_STATE) then
        for _, knob in pairs(KNOBS) do
            knob.state = KNOB_STATE.OFF
        end

        -- Initialize thief's call
        if CUR_STATE == STATES.END_LEVEL_ZERO then
            KNOBS[1].state = KNOB_STATE.INCOMING
            CALLS[1].state = CALL_STATE.UNUSED
            CALLS[1].src = KNOBS[1]
            if (FRAME_COUNTER % 90 == 0) then sfx(13, 60, 18, 3, 6) end
        end

        -- Show 'Day's over' screen
        sleep(3)
    end
end

-- draws
function draw()
    cls()
    if has_value(PLAYABLE_STATES, CUR_STATE) then
        draw_game()
        -- Should not have timer when the thief calls
        if CUR_STATE ~= STATES.CALL_THIEF then draw_timer(LEVELS[CUR_STATE].time) end
    elseif (CUR_STATE == STATES.MAIN_MENU) then
        draw_main_menu()
    elseif (CUR_STATE == STATES.CUTSCENE_ZERO_1) then
        draw_cutscene_zero_one()
    elseif (CUR_STATE == STATES.CUTSCENE_ZERO_2) then
        draw_cutscene_zero_two()
    elseif (CUR_STATE == STATES.CUTSCENE_ZERO_3) then
        draw_cutscene_zero_three()
    elseif (CUR_STATE == STATES.CUTSCENE_ZERO_4) then
        draw_cutscene_zero_four()
    elseif (CUR_STATE == STATES.CUTSCENE_THIEF_1) then
        draw_cutscene_thief_one()
    elseif (CUR_STATE == STATES.CUTSCENE_THIEF_2) then
        draw_cutscene_thief_two()
    elseif (CUR_STATE == STATES.CUTSCENE_THIEF_3) then
        draw_cutscene_thief_three()
    elseif (CUR_STATE == STATES.CUTSCENE_THIEF_4) then
        draw_cutscene_thief_four()
    elseif (CUR_STATE == STATES.CUTSCENE_THIEF_5) then
        draw_cutscene_thief_five()
    elseif has_value({STATES.SELECT_MENU_1, STATES.SELECT_MENU_2}, CUR_STATE) then
        draw_select_menu()
    elseif has_value(END_LEVEL_STATES, CUR_STATE) then
        draw_end_level()
    elseif (CUR_STATE == STATES.CUTSCENE_NEWS) then
        draw_cutscene_news()
    elseif (CUR_STATE == STATES.CUTSCENE_FINAL) then
        draw_cutscene_final()
    end
end

-- inits
function init()
    reset()
    KNOBS = init_knobs()
    CALLS = init_calls()
end

function setup_level()
    MESSAGES = generate_messages(LEVELS[CUR_STATE].messages)
    SECONDS_PASSED = 0
end

function reset()
    music(4)

    -- reset state
    CUR_STATE = STATES.MAIN_MENU

    -- reset counters
    for _, level in pairs(LEVELS) do
        level.missed = 0
        level.interrupted = 0
        level.wrong = 0
        level.solution = nil
    end
end

function reset_call_segments(call)
    call.rope_segments[1] = {x = call.src.x, y = call.src.y}
    call.rope_segments[#call.rope_segments] = {x = call.dst.x, y = call.dst.y}
end

-- starts the game
init()

